mapper.select:
select bno, title, writer, view_cnt, reg_date
	from board
	order by bno DESC, reg_date DESC


			limit 0, #{listSize}


			limit #{rowNum}, #{listSize}

select bno, title, content, writer, view_cnt, reg_date, up_date
	from board
	where bno = #{bno}

mapper.update:
update board
	set title = #{title}, content = #{content}, up_date = now()
	where bno = #{bno}

update board
	set view_cnt = view_cnt + 1
	where bno = #{bno}
